function startTimeout() {
    refresh_token.periodical(300000)
}

function refresh_token() {
    var a = "index.php?option=com_opac&task=refresh_token&format=raw";
    var b = new Request.HTML({
        url: a,
        method: "get",
        update: $("span_tok")
    });
    b.send()
}

function update_dispo_anx(d, c, e) {
    var a = "index.php?option=com_opac&task=choixdispoanx&format=raw&mode=" + e + "&listeanx=" + d;
    var b = new Request.HTML({
        url: a,
        method: "get",
        onComplete: function(f) {
            if (this.isSuccess) {
                $("lib_dispo_anx").set("text", c)
            }
        }
    });
    b.send()
}
window.addEvent("domready", function() {
    var a = new Element("input");
    if (!("placeholder" in a)) {
        $$("input[placeholder]").each(function(b) {
            new OverText(b, {
                textOverride: b.get("placeholder")
            })
        })
    }
    startTimeout()
});
