function del_ntc_panier(b, d) {
    var a = "index.php?option=com_opac&task=DelNtcPanier&format=ajax&num_ntc=" + b + "&Itemid=" + d;
    var c = new Request.HTML({
        url: a,
        method: "get",
        update: $("opac_panier"),
        onSuccess: function() {
            var f = $("opac_panier").getElements(".hasTipTrunc");
            var e = new Tips(f)
        }
    });
    c.send()
}

